///////////////////////////////////////////////////////////
// CONFIG
///////////////////////////////////////////////////////////
const bool HAS_GYRO = true;
const bool HAS_ACCEL = false;
const tSensors gyroPort = S3;
const tSensors accelPort = S2;

// leftSide and rightSide are the two functions which you must create. They must be written by you, since they depend on your robot.
void leftSide(int val){
  motor[motorC] = -val;
}

void rightSide(int val){
  motor[motorB] = -val;
}

#include "libftc-util.c"
#include "drivers/HTMC-driver.h"

// This function is called before you start the match. initalize your servos here.
void initialize(){
  libftc_init();
}

void cal()
{
  rightSide(5);
  leftSide(-5);
  HTMCstartCal(compass);
  ClearTimer(T1);
  while (time100[T1] < 200){};
  HTMCstopCal(compass);
  rightSide(0);
  leftSide(0);
}

task main(){
  initialize();
  wait10Msec(100);
  int speed = 10;
  leftSide(-10);
  rightSide(10);
  while (HTMCreadHeading(compass) > 3){}
  leftSide(0);
  rightSide(0);
  while (speed < 101){
    leftSide(speed);
    rightSide(-speed);
    wait1Msec(50);
    float gyroVal = 0;
    ClearTimer(T1);
    int avg_speed = 0;
    int count = 0;
    while (HTMCreadHeading(compass) > 2) {
      float deltaGyro = (float)(SensorValue[gyroPort]-gyroOff)/80.0;
      avg_speed += (SensorValue[gyroPort]-gyroOff);
      gyroVal += deltaGyro;
      wait1Msec(5);
      ++count;
    }
    float time = time1[T1]/1000.0;
    writeDebugStreamLine("%f %f",360.0/time,(float)avg_speed/(float)count);
    PlayImmediateTone(1000,10);
    //AddToDatalog(speed);
    //AddToDatalog(gyroVal);
    speed += 10;
    leftSide(0); rightSide(0);
    wait1Msec(1000);
  }
  //SaveNxtDatalog();
  writeDebugStreamLine("-------------------");
}